{"query":"#Query the organization and its members
#Returns ID that can be used in subsequent queries
#and each members role

query {
	login {
		account {
			id
		}
	}
}
"}

{"query":"#Query the organization and its members
#Returns ID that can be used in subsequent queries
#and each members role

query {
	login {
		organization {
			id
			name
			created
			members {
				user {
					email
				}
				role
			}
		}
	}
}
"}

{"query":"#Retrieves all organizations and roles a user is a member of

query {
	login {
		membership {
			role
			organization {
				id
				name
				account {
					id
					email
				}
				created
			}
		}
	}
}
"}

{"query":"#For API Authentication:
#only the Key is returned.
#Secret is only retrievable once upon creation

query {
	login {
		accessKeys {
			created
			enabled
			key
			lastUsed
			__typename
		}
	}
}
"}

{"query":"#List of supported application types and their default port for
#for service creation

query {
	applicationTypes {
		id
		name
		description
		port
	}
}
"}

{"query":"#Gets the current notification settings on users account
#Notifications are used to deliver device and service online/offline events

query {
	login {
		notificationSettings {
			emailNotifications
			desktopNotifications
			urlNotifications
			notificationEmail
			notificationUrl
		}
	}
}
"}

{"query":"#Access keys are used to authenticate you with our API.
#You can create a new key or delete an existing key at any time.
#You can also temporarily disable a key.
#If you lose or forget your secret key, you cannot retrieve it.
#There is a limit of #2 access keys.
#No variables are required to be passed in

mutation {
	createAccessKey {
		key
		secret
		__typename
	}
}
"}

{"query":"#Access keys are used to authenticate you with our API.
#You can enable or disable keys at any time.

#This example shows how to provide operation names for mutations
#and pass in query variables

mutation changeAccessKey($key: String!, $enable: Boolean!) {
	updateAccessKey(key: $key, enabled: $enable) {
		created
		key
		enabled
		lastUsed
	}
}
","variables":{"key":"2873","enable":true},"operationName":"changeAccessKey"}

{"query":"#Access keys are used to authenticate you with our API.
#You can create a new key or delete an existing key at any time.

#You must pass the key to delete from the account
#Keys are not recoverable once deleted

mutation {
	deleteAccessKey(key: "")
}
"}

{"query":"#Returns any custom attributes added to the User, Device or Service

query {
	login {
		attributes
		devices {
			hasMore
			total
			items {
				attributes
				services {
					attributes
				}
			}
		}
	}
}
"}

{"query":"#This mutation sets a custom attribute of given value to
#a User, Device or Service. 
#User is ID in {login{account{id}}}


mutation {
  setAttribute(
    name: "CustomAttribute",
    value: "ValueOfAttribute",
    serviceId: "2AEA1ECC-0E684D40"
  )
}"}

{"query":"#This mutation updates the category A on a Device.
#Attributes can be applied to User, Device or Service.

mutation {
	setAttribute(
		name: "$remoteit.categoryA"
		value: "my category A value"
		serviceId: "80:00:00:00:12:34:56:78"
	)
}
"}

{"query":"#This example passes in a json formatted object to a 
#custom fuction name to set any number of attributes

mutation UpdateMultipleAttributes($attributes: Object!, $serviceId: String!) {
	setAttributes(attributes: $attributes, serviceId: $serviceId)
}
","variables":{"attributes":{"$remoteit":{"categoryA":"Uptime: 1h, 06m","categoryB":"Version: 1.0","categoryC":"Serial Number: x292jsu2","categoryD":"MAC: AUWS83729A"}},"serviceId":"80:00:00:00:01:12:12:12"},"operationName":"UpdateMultipleAttributes"}

{"query":"#to delete tags you must set the value to null

mutation {
	setAttribute(
		name: "CustomAttribute"
		value: null
		serviceId: "80:00:00:00:12:34:56:78"
	)
}
"}

{"query":"#To add a tag by name to a given device.
#You can add existing or new tags with this mutation
#Use Get Devices to retrieve the ID for serviceID field.

mutation {
	addTag(name: "", serviceId: "")
}
"}

{"query":"#To add multiple tags by name to a list of devices.
#You can add existing or new tags with this mutation
#Use Get Devices to retrieve the ID for serviceID field.

mutation {
	addTag(
		name: ["hello", "world"]
		serviceId: ["80:00:01:01:01:00:01:01", "80:00:01:01:01:00:01:02"]
	)
}
"}

{"query":"#To remove multiple tags by name.
#If the tags are associated with devices or custom roles they will be removed.

mutation {
	deleteTag(name: ["staging", "production", "qa"])
}
"}

{"query":"#This query shows all the active connection sessions including
#when, where, who and what devices they are connected to

query {
	login {
		sessions {
			id
			timestamp
			endpoint {
				proxy
				geo {
					countryName
				}
			}
			user {
				email
			}
			target {
				id
				name
				port
				application
				device {
					id
					name
				}
			}
		}
	}
}
"}

{"query":"#Used to disconnect any active proxy connection

mutation Disconnect($serviceId: String!, $connectionId: String!) {
	disconnect(serviceId: $serviceId, connectionId: $connectionId)
}
