{"query":"#This query returns logs for all devices and a list of device events
#How far logs can be retrieved is determined by your subscription level

query getEvents(
	$size: Int
	$from: Int
	$minDate: DateTime
	$maxDate: DateTime
	$types: [EventType!]
) {
	login {
		events(
			size: $size
			from: $from
			minDate: $minDate
			maxDate: $maxDate
			types: $types
		) {
			hasMore
			total
			items {
				type
				owner {
					email
				}
				actor {
					email
				}
				target {
					created
					id
					name
				}
				users {
					email
				}
				timestamp
			}
		}
	}
}
","variables":{"size":1000,"from":0,"minDate":"2021-06-01","maxDate":"2021-07-01","types":["DEVICE_CONNECT"]},"operationName":"getEvents"}

{"query":"#This query returns a url to download logs for
#all devices and a list of device events
#How far logs can be retrieved is determined by your subscription level

{
	login {
		eventsUrl(
			minDate: "2022-01-01"
			maxDate: "2022-02-01"
			types: [DEVICE_CONNECT]
		)
	}
}
"}

{"query":"#This query returns logs for a given list of devices and a list of device events
#How far logs can be retrieved is determined by your subscription level

query getEvents(
	$deviceID: [String!]!
	$minDate: DateTime
	$maxDate: DateTime
	$types: [EventType!]
) {
	login {
		device(id: $deviceID) {
			events(minDate: $minDate, maxDate: $maxDate, types: $types) {
				hasMore
				total
				items {
					state
					timestamp
					target {
						name
						id
					}
					... on DeviceStateEvent {
						externalAddress
						geo {
							connectionType
							isp
						}
					}
				}
			}
		}
	}
}

#variables
","variables":{"deviceID":["80:XX:00:00:01:02:03:04"],"minDate":"2021-06-01","maxDate":"2021-07-01","types":["DEVICE_STATE"]},"operationName":"getEvents"}

{"query":"#This query returns a url for downloading logs for a
#given list of devices and a list of device events
#How far logs can be retrieved is determined by your subscription level

query getEventsUrl(
	$deviceID: [String!]!
	$minDate: DateTime
	$maxDate: DateTime
	$types: [EventType!]
) {
	login {
		device(id: $deviceID) {
			eventsUrl(minDate: $minDate, maxDate: $maxDate, types: $types)
		}
	}
}
","variables":{"deviceID":["80:XX:00:00:01:02:03:04"],"minDate":"2021-06-01","maxDate":"2021-07-01","types":["DEVICE_STATE"]},"operationName":"getEventsUrl"}

{"query":"{
	login {
		feed(start: "2022-04-01T00:00:00.000Z", end: "2022-04-14T23:59:59.000Z") {
			start
			end
			duration
			items {
				__typename
				start
				end
				duration
				... on DeviceOfflineItem {
					device {
						id
						name
					}
					intervals {
						start
						end
						duration
						offline {
							externalAddress
						}
						online {
							externalAddress
						}
					}
				}
				... on UserLoginItem {
					user {
						email
					}
					events {
						timestamp
					}
				}
				... on UserConnectionItem {
					user {
						email
					}
					service {
						id
						name
						title
						device {
							id
							name
						}
					}
					# each conection interval if listed below
					intervals {
						# start of the connection (or period start)
						start
						# end of the connection (or period end)
						end
						# duration of the connection
						duration
						# the connect and disconnect events can be further queried
						connect {
							proxy
							sourceAddress
							sourceGeo {
								state
								city
								country
							}
						}
					}
				}
			}
		}
	}
}
"}

{"query":"#Gets the list of devices limited to the first 1000
#hasMore - if there are more devices in the account than requested
#total - total number of devices based on query filter and account
#Retrieves details of each devices such as
#id, online/offline state, lastReported Date
#which users have accounts
#tags and attributes associated with each device

query {
	login {
		devices {
			hasMore
			total
			items {
				id
				name
				state
				lastReported
				access {
					user {
						email
					}
				}
				tags {
					name
				}
				attributes
			}
		}
	}
}
"}

{"query":"#Gets the list of devices limited to the size argument
#hasMore - if there are more devices in the filtered list than requested
#total - total number of devices based on query filter and account
#Retrieves details of each devices such as
#id, online/offline state, lastReported Date
#Status and Category Fields

query {
	login {
		devices(size: 10, sort: "-created", from: 0, name: "pi") {
			hasMore
			total
			items {
				id
				name
				state
				lastReported
				categoryA
				statusA
				categoryB
				statusB
				categoryC
				statusC
				categoryD
				statusD
			}
		}
	}
}
"}

{"query":"#Gets the list of devices and their services limited to the size argument
#hasMore - if there are more devices in the filtered list than requested
#total - total number of devices based on query filter and account
#Retrieves details of each devices such as
#id, online/offline state, lastReported Date
#Each service under the device and you can filter on multiple arguments
#This example finds all ssh "applicationType:28" services under each device with "pi" in the device name

query {
	login {
		devices(size: 10, sort: "-created", from: 0, name: "pi") {
			hasMore
			total
			items {
				id
				name
				state
				lastReported
				services(application: 28) {
					id
					name
					host
					enabled
					port
					state
					sessions {
						user {
							email
						}
						timestamp
					}
				}
			}
		}
	}
}
"}

{"query":"#Another method of returning devices and services
#Every Device and Service is considered an endpoint
#Endpoints contain additional meta data
#including % of device being online and available
#its calculated geo location
#version of the remoteit deamon running 

{
	login {
		endpoints {
			id
			name
			availability
			state
			geo {
				countryName
				stateName
			}
			externalAddress
			expired
			quality
			version
		}
	}
}
"}

{"query":"#Mutation to share a device and all services 
#You can determine whether or not scripting is available to the user separate from services

mutation {
	share(
		deviceId: "80:00:00:00:12:34:56:78"
		email: "email@example.com"
		scripting: false
		notify: true
		action: ADD
	)
}
"}

{"query":"#Mutation to share a device and all services 
#You can determine whether or not scripting is available to the user separate from services

mutation {
	share(
		deviceId: "80:00:00:00:12:34:56:78"
		email: "email@example.com"
		notify: true
		action: REMOVE
	)
}
"}

{"query":"#This mutation allows you to leave an individual device that has been shared to you

mutation {
	share(
		deviceId: "80:00:00:00:01:02:03:04"
		email: "YourEmail@email.com"
		action: LEAVE
	)
}
"}

{"query":"#This mutation allows you to modify or share an existing single device share
#For the given deviceID
#share to user email
#Notify the user the device has been shared
#do not Allow for bulk scripting
#Allow user to connect to service 80:00:00:00:01:01:05:06
#Remove service from the previous share event

mutation {
	share(
		deviceId: "80:00:00:00:01:01:01:02"
		email: "UserToShareTo@email.com"
		scripting: false
		notify: true
		services: [
			{ serviceId: "80:00:00:00:01:01:05:06", action: ADD }
			{ serviceId: "80:00:00:00:01:01:04:07", action: REMOVE }
		]
	)
}
"}

{"query":"#You can pass service ID or device ID into this mutation
#Services will be deleted if they are found
#Devices will only be deleted if they are offline

mutation{
	deleteDevice(deviceId:"")
}"}

{"query":"#You can pass service ID or device ID into this mutation
#success will be determined by your permissions to the provided ID

mutation {
	renameService(name: "New Name", serviceId: "")
}
"}
