{"query":"#Query the organization and its members
#Returns ID that can be used in subsequent queries
#and each members role

query {
	login {
		organization {
			# this is your organization, not any that your belong to as a member
			id
			name
			created
			roles {
				id
				name
				permissions
			}
			members {
				user {
					email
				}
				customRole {
					name
				}
			}
		}
	}
}
"}

{"query":"#Retrieves all organizations and roles a user is a member of

query {
	login {
		membership {
			# This is your role and permissions
			customRole {
				id
				name
				permissions
			}
			organization {
				id
				name
				account {
					id
					email
				}
				created
			}
		}
	}
}
"}

