{"query":"#This query returns logs for all devices and a list of device events
#How far logs can be retrieved is determined by your subscription level

query getEvents(
	$size: Int
	$from: Int
	$minDate: DateTime
	$maxDate: DateTime
	$types: [EventType!]
) {
	login {
		events(
			size: $size
			from: $from
			minDate: $minDate
			maxDate: $maxDate
			types: $types
		) {
			hasMore
			total
			items {
				type
				owner {
					email
				}
				actor {
					email
				}
				target {
					created
					id
					name
				}
				users {
					email
				}
				timestamp
			}
		}
	}
}
","variables":{"size":1000,"from":0,"minDate":"2021-06-01","maxDate":"2021-07-01","types":["DEVICE_CONNECT"]},"operationName":"getEvents"}

{"query":"#This query returns logs for all connect events based on your permission level.
#How far logs can be retrieved is determined by your subscription level

query getEvents(
	$size: Int
	$from: Int
	$minDate: DateTime
	$maxDate: DateTime
	$types: [EventType!]
) {
	login {
		events(
			size: $size
			from: $from
			minDate: $minDate
			maxDate: $maxDate
			types: $types
		) {
			hasMore
			total
			items {
				... on DeviceConnectEvent 			{
					actor {
						email
					}
					#source info is the connecting person. If they are using the web interface and not the desktop, this will be the proxy server information
					sourceAddress
					sourceType
					sourceGeo {
						connectionType
					}
				}
				target {
					created
					id
					name
				}
				timestamp
			}
		}
	}
}
","variables":{"size":1000,"from":0,"minDate":"2022-06-01","maxDate":"2022-07-31","types":["DEVICE_CONNECT"]},"operationName":"getEvents"}

