{
	login {
		endpoints {
			id
			name
			availability
			state
			geo {
				countryName
				stateName
			}
			externalAddress
			expired
			quality
			version
		}
	}
}
"}

{"query":"#Mutation to share a device and all services 
#You can determine whether or not scripting is available to the user separate from services

mutation {
	share(
		deviceId: "80:00:00:00:12:34:56:78"
		email: "email@example.com"
		scripting: false
		notify: true
		action: ADD
	)
}
"}

{"query":"#Mutation to share a device and all services 
#You can determine whether or not scripting is available to the user separate from services

mutation {
	share(
		deviceId: "80:00:00:00:12:34:56:78"
		email: "email@example.com"
		notify: true
		action: REMOVE
	)
}
"}

{"query":"#This mutation allows you to leave an individual device that has been shared to you

